const modal=document.getElementById("contact-modal"),contactLink=document.getElementById("contact-link"),closeBtn=document.querySelector(".close");contactLink.addEventListener("click",e=>{e.preventDefault(),modal.style.display="block"}),closeBtn.addEventListener("click",()=>{modal.style.display="none"}),window.addEventListener("click",e=>{e.target===modal&&(modal.style.display="none")});const sanitizeInput=e=>e.replace(/</g,"&lt;").replace(/>/g,"&gt;");document.getElementById("contact-form").addEventListener("submit",async e=>{e.preventDefault();let t=sanitizeInput(document.getElementById("email").value),n=sanitizeInput(document.getElementById("subject").value),a=sanitizeInput(document.getElementById("message").value);try{let l=await fetch("UPDATE_LINK.hf.space/sendEmail",{method:"POST",headers:{"Content-Type":"application/json"},body:JSON.stringify({email:t,subject:n,message:a})});if(l.ok)alert("Message sent successfully!"),modal.style.display="none";else throw Error("Failed to send message")}catch(s){alert("Failed to send message. Please try again."),console.error("Error:",s)}});